DownloadPythonLibraries.Block:
# Defining variables for DownloadPythonLibraries
$target_directory_var = {{"C:\Users\tommarcusbrut\AppData\Local\Programs\Python\Python313\Lib\site-packages"}} # Set download directory
$Libraries = @({{library_name}}) # List of libraries

try {
    Write-Host "Executing steps for block: DownloadPythonLibraries"
    # --- Step: Verify pip Installation ---
    if (-not (Get-Command pip -ErrorAction SilentlyContinue)) {
        Write-Error "pip is not installed. Please install pip and try again."
        exit
    }
    Write-Host "pip command verified successfully. Proceeding with downloads."

    # --- Step: Download Python Library ---
    Write-Host "Starting library download loop..."
    foreach ($lib in $Libraries) {
        Write-Host "Attempting to download library: $lib to $target_directory_var"
        pip download --no-deps --dest "$target_directory_var" $lib
        Write-Host "Successfully downloaded $lib to $target_directory_var"
    }
    Write-Host "Library download loop finished."

} catch {
    Write-Warning "An error occurred during block 'DownloadPythonLibraries': $($_.Exception.Message)"
} finally {
    Write-Host "Finished executing block 'DownloadPythonLibraries' (finally)."
}
